pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;
import { IERC20 } from './interfaces/IERC20.sol';
import { IVault } from './interfaces/IVault.sol';
import { IAsset } from './interfaces/IAsset.sol';
import { IStaticATokenLM } from './interfaces/IStaticATokenLM.sol';
import { ILendingPool } from './interfaces/ILendingPool.sol';
import { IFlashLoanReceiver } from './interfaces/IFlashLoanReceiver.sol';
import { DataTypes } from './interfaces/DataTypes.sol';

contract LinearPoolSwap is IFlashLoanReceiver {

    IVault private vault;
    IERC20 private token;
    IStaticATokenLM private wrappedHToken;
    bytes32 private poolId;
    ILendingPool private lendingPool;
    uint256 constant UINT256_MAX = type(uint256).max;
    enum op { WIND, UNWIND }

    constructor(address vaultAddress, address lendingPoolAddress, address tokenAddress, address wrappedHTokenAddress,  bytes32 _poolId) public {
        vault = IVault(vaultAddress);
        lendingPool = ILendingPool(lendingPoolAddress);
        token = IERC20(tokenAddress);
        wrappedHToken = IStaticATokenLM(wrappedHTokenAddress);
        poolId = _poolId;
    }

    // Decrease USDC tokens in pool and increase wrapped hUSDC
    function windXTimes(uint8 counter, uint256 amount) public {
        token.transferFrom(msg.sender, address(this), amount);

        for (uint8 i=0; i < counter; i++) {
            wind();
        }

        token.transfer(msg.sender, token.balanceOf(address(this)));
    }

    function wind() public {

        token.approve(address(wrappedHToken), UINT256_MAX);

        wrappedHToken.deposit(address(this), token.balanceOf(address(this)), 0, true);
        wrappedHToken.approve(address(vault), UINT256_MAX);

        bytes memory userDataEncoded = "";

        IVault.SingleSwap memory singleSwap = IVault.SingleSwap(
            poolId,
            IVault.SwapKind.GIVEN_IN,
            IAsset(address(wrappedHToken)),
            IAsset(address(token)),
            wrappedHToken.balanceOf(address(this)),
            userDataEncoded
        );
        IVault.FundManagement memory fundManagement = IVault.FundManagement(
            address(this),
            false,
            payable(address(this)),
            false
        );

        uint256 limit = wrappedHToken.balanceOf(address(this)) * 90 / 100;
        vault.swap(singleSwap, fundManagement, limit, UINT256_MAX);



        // USDC 52,268,702.48 hUSDC 48,764.24 wind 1 time 100 USDC
        // USDC 52,268,602.47 hUSDC 48,864.06

        // USDC 52,268,687.32 hUSDC 48,779.36 wind 10 times 100 USDC
        // USDC 52,267,680.43 hUSDC 49,784.35

        // USDC 52,267,680.43 hUSDC 49,784.35 wind 10 times 500 USDC
        // USDC 52,262,645.99 hUSDC 54,809.32

        // USDC 52,262,645.99 hUSDC 54,809.32 wind 10 times 500 USDC
        // USDC 52,257,611.54 hUSDC 59,834.28
    }

    // Increase USDC tokens in pool and decrease wrapped hUSDC
    function unwindXTimes(uint8 counter, uint256 amount) public {
        token.transferFrom(msg.sender, address(this), amount);

        for (uint8 i=0; i < counter; i++) {
            unwind();
        }

        token.transfer(msg.sender, token.balanceOf(address(this)));
    }

    function unwind() public {
        token.approve(address(vault), UINT256_MAX);

        bytes memory userDataEncoded = "";

        IVault.SingleSwap memory singleSwap = IVault.SingleSwap(
            poolId,
            IVault.SwapKind.GIVEN_IN,
            IAsset(address(token)),
            IAsset(address(wrappedHToken)),
            token.balanceOf(address(this)),
            userDataEncoded
        );
        IVault.FundManagement memory fundManagement = IVault.FundManagement(
            address(this),
            false,
            payable(address(this)),
            false
        );

        uint256 limit = token.balanceOf(address(this)) * 90 / 100;
        vault.swap(singleSwap, fundManagement, limit, UINT256_MAX);

        wrappedHToken.withdraw(address(this), wrappedHToken.balanceOf(address(this)), true);

        // USDC 52,268,602.47 hUSDC 48,864.06 unwind 1 time 100 USDC
        // USDC 52,268,702.33 hUSDC 48,764.39

        // USDC 52,268,702.33 hUSDC 48,764.39 unwind 10 times 100 USDC
        // USDC 52,269,694.21 hUSDC 47,774.37

        // USDC 52,257,611.54 hUSDC 59,834.28 unwind 10 times 500 USDC
        // USDC 52,262,570.98 hUSDC 54,884.18
    }

    function flashLoanWind(uint256 amount) public {
        address[] memory tokens = new address[](1);
        tokens[0] = address(token);

        uint256[] memory amounts = new uint256[](1);
        amounts[0] = amount;

        uint256[] memory modes = new uint256[](1);
        modes[0] = 0;

        bytes memory params = abi.encode(op.WIND, msg.sender);

        lendingPool.flashLoan(address(this), tokens, amounts, modes, address(this), params, 0);

        // USDC 52,262,570.98 hUSDC 54,884.18 flashloanWind 1000 USDC
        // USDC 52,262,570.98 hUSDC 54,884.18 reentrancy error ???
    }

    function flashLoanUnwind(uint256 amount) public {
        address[] memory tokens = new address[](1);
        tokens[0] = address(token);

        uint256[] memory amounts = new uint256[](1);
        amounts[0] = amount;

        uint256[] memory modes = new uint256[](1);
        modes[0] = 0;

        bytes memory params = abi.encode(op.UNWIND, msg.sender);

        lendingPool.flashLoan(address(this), tokens, amounts, modes, address(this), params, 0);

        // USDC 52,262,569.88 hUSDC 55,882.32 flashloanWind 1000 USDC
        // USDC 52,262,570.98 hUSDC 54,884.18 reentrancy error ???
    }

    function executeOperation(
        address[] calldata assets,
        uint256[] calldata amounts,
        uint256[] calldata premiums,
        address initiator,
        bytes calldata params
    ) external override returns (bool) {
        op decodedData;
        address originalSender;
        (decodedData, originalSender) = abi.decode(params,(op, address));
        //token.transfer(originalSender, 50_000_000);
        if (decodedData == op.WIND) {
            wind();
        } else if (decodedData == op.UNWIND) {
            unwind();
        }
        uint256 currentBalance = token.balanceOf(address(this));  
        uint256 returnValue = amounts[0] + premiums[0];
       // token.transferFrom(originalSender, address(this), returnValue);

        if (returnValue > currentBalance) {
            token.transferFrom(originalSender, address(this), returnValue - currentBalance);
        } else if (returnValue < currentBalance) {
            token.transfer(originalSender, currentBalance - returnValue);
        }

        token.approve(address(lendingPool), returnValue);
        return true;
    }
}
